import{_ as e}from"./four.3d806b1d.js";import{_ as n,o as r,c as a,d as i}from"./index.f7895191.js";import"https://unpkg.com/@lottiefiles/lottie-player@latest/dist/lottie-player.js";const s={name:"DayFour.vue"},t={class:"container"},h=i('<img src="'+e+'" alt="" style="width:100%;"><div><hr><h2>4. Dezember 2022</h2><hr></div><div><br><p> 14. Mai 2022 - unser erstes Treffen Teil III </p><br><p>Du wei\xDFt bestimmt, welches Gespr\xE4chsthema da kommt \u{1F606} *Kinder, hust, hust*</p><br><p>Nein, nat\xFCrlich nur ein Spa\xDF, aber es war ein sehr nettes Gespr\xE4ch - die Zeit ist wie im Fluge vergangen.</p><br><p>Die Zeit verging sogar so schnell, dass auf einmal der Kellner da stand und uns indirekt gebeten hat, dass wir bezahlen und dann gehen.</p><br><p> Ein kurzer Blick auf meine Uhr, tats\xE4chlich, es war schon sp\xE4t geworden - also habe ich die Rechnung \xFCbernommen und wir sind dann rausgegangen. Du hast jemanden erkannt und die Person gegr\xFC\xDFt, war sicher jemand aus Ottenthal. </p><br><p> Danach war der nette Abend auch schon vorbei. Eis wollten wir keines mehr, aber daf\xFCr war es schon sowieso zu sp\xE4t - also sind wir dann gemeinsam Richtung Auto gegangen. Dabei habe ich meinen afghanischen Freund von der Pizzeria getroffen. </p><br><p> Ich wei\xDF noch, dass ich dich zu deinem Auto begleiten wollte, du aber gesagt hast, dass das schon okay w\xE4re, wenn wir uns auf der Hauptstra\xDFe verabschieden w\xFCrden. Also Bussi links, Bussi rechts, eine kleine Umarmung und dann hab ich noch kurz geschaut, dass du eh sicher \xFCber die Stra\xDFe kommst. </p><br><p> Ich war dann nach guten 2 Minuten auch bei meinem Auto, habe die T\xFCr ge\xF6ffnet und habe kurz durchgeatmet und mich gesammelt. </p><br><p> Nach einem kurzen Augenblick bin ich dann auch heimgefahren und habe w\xE4hrenddessen viel nachgedacht. \u{1F60A} </p><br><hr><br><p>Frage: </p><br><p>Welche Farbe hatte damals mein Hemd? (F\xFCr die richtige Antwort bekommmst du 1 Punkt)</p><br><p>A: Schwarz</p><p>B: Grau</p><p>C: Blau</p><br><p>Update:</p><br><p>Du liegst sicher neben mir im Bett, du kriegst keine Antwort von mir. \u{1F92A}</p></div>',3),d=[h];function c(u,o,p,m,b,g){return r(),a("div",t,d)}const f=n(s,[["render",c]]);export{f as default};
