import{_ as e}from"./nine.3cb333d1.js";import{_ as n,o as i,c as a,d as h}from"./index.b5282458.js";import"https://unpkg.com/@lottiefiles/lottie-player@latest/dist/lottie-player.js";const r={name:"DayEight.vue"},s={class:"container"},t=h('<img src="'+e+'" alt="" style="width:100%;"><div><hr><h2>9. Dezember 2022</h2><hr></div><div><br><p> Salzburg \u{1F9C2}\u{1F3F0} - Teil I </p><br><p>Ich habe meine Koffer gepackt und schnell nochmal nachgedacht, ob ich denn was wichtiges vergessen habe. Morgen ist es dann endlich soweit, wir fahren nach Salzburg, unser erster gemeinsamer Urlaub.</p><br><p>Ich wei\xDF noch, als du mir vorgeschlagen hast, ob wir nicht f\xFCr 2 Tage nach Salzburg fahren m\xF6chten, da deine Schwester verhindert ist - ich habe das anfangs f\xFCr einen Scherz gehalten. \u{1F606} Aber als ich dann realisiert habe, dass das kein Scherz ist, da hat mir die Idee schon sehr gut gefallen.</p><br><p>Zu diesem Zeitpunkt war mein letzter Urlaub schon fast 2 Jahre her, aufgrund bestimmter Ereignisse ist der Urlaub in den letzten Jahren ausgefallen.</p><br><p> Ich musste eigentlich auch nicht lange \xFCberlegen, wir machen das, wir fahren nach Salzburg, auch wenn wir uns zu dem Zeitpunkt noch nicht soooo lange gekannt haben bzw. wir noch nichtmal in einem Bett geschlafen haben. \u{1F606} </p><br><p> Ich habe nochmal alles gecheckt, ich habe nichts wichtiges vergessen - doch, ich habe was vergessen, wie sich sp\xE4ter heraustellt... Tag der Abfahrt, in 20 Minuten bist du da - ich stehe in der K\xFCche, die Koffer stehen beim Eingang, mein Blick schweift immer wieder aus dem Fenster....gleich gehts los. </p><br><p> Meine Eltern fahren auch gleich, haben einen Termin - sie wollten dir auch noch einen sch\xF6nen Urlaub w\xFCnschen, aber das wird knapp - sie steigen in das Auto und fahren los und auf einmal sehe ich dich schon, wie du in die Stra\xDFe einbiegst. Ist sich doch noch alles ausgegangen. </p><br><p> Die redest noch mit ihnen und ich packe meine Koffer und gehe zu dir raus...schnell die Koffer in den Kofferraum gepackt und dann gehts auch schon los...</p><br><p> Abfahrt.. </p><br></div>',3),c=[t];function d(o,l,u,g,b,m){return i(),a("div",s,c)}const _=n(r,[["render",d]]);export{_ as default};
